mapper.select:
select bno,writer,writer_picture,workshopName,title,category,regdate,updatedate,replyCnt,likeCnt,readCnt, price,salesCnt,color,allsize,content,banner,mainImage,status,stock, shipPrice,shipCompany,shipReturnPrice,shipChangePrice,shipReturnPlace,shipDays,shipInfo from (select /*+ index_desc(tbl_proBoard pk_proBoard) */ rownum rn, bno,writer,writer_picture,workshopName,title,category,regdate,updatedate,replyCnt,likeCnt,readCnt, price,salesCnt,color,allsize,content,banner,mainImage,status,stock, shipPrice,shipCompany,shipReturnPrice,shipChangePrice,shipReturnPlace,shipDays,shipInfo from tbl_proBoard where
rownum <= #{pageNum}*#{amount}
ORDER BY #{sort} DESC
) where rn>(#{pageNum}-1) * #{amount}

mapper.update:
update tbl_proBoard 
set readCnt = readCnt + 1
where bno=#{bno}

update tbl_proBoard
set 
	writer_picture=#{member_picture}, 
	workshopName=#{member_nick}
where writer=#{member_id}

update tbl_proBoard 
set replycnt = replycnt + #{amount}
where bno=#{bno}
